import React, { useState, useEffect } from "react";
import axios from 'axios';
import { API_URL } from '../API';
import { useAppContext } from "./context/appContext";


const BookList = () => {
  const [books, setBooks] = useState([]);
  const { favorites, addToFavorites } = useAppContext();

  useEffect(() => {
    axios.get(API_URL)
      .then(res => {
        setBooks(res.data);
      })
      .catch(err => console.log(err));
  }, []);

  const handleAddToFavorites = (book) => {
    addToFavorites(book);
    // You can add additional logic here after adding to favorites, if needed
  };

  return (
    <div className="book-list">
      {books.map((book) => (
        <div key={book.id} className="book">
          <div><h3>{book.title}</h3></div>
          <div className="imgs"><img src={book.cover_picture} alt="#" /></div>
          <div>
            <button onClick={() => handleAddToFavorites(book)}>
              Add to favorites
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default BookList;
