import React from "react";
import "../App.css";
import { useState, useEffect } from "react";
import axios from 'axios';
import { API_URL } from '../API';

const BookList = () => {
    const [books, setBooks] = useState([]);
  
    useEffect(() => {
      axios.get(API_URL)
        .then(res => {
          // Update the state with fetched books data
          setBooks(res.data.map(book => ({
            ...book,
            cover_picture: book.cover_picture.replace(/\\\//g, "/") // Modify the image URLs
          })));
        })
        .catch(err => console.log(err));
    }, []);
  
    return (
      <div className="book-list">
        {
          books.map((book) => (
            <div key={book.id} className="book">
              <div><h3>{book.title}</h3></div>
              <div className="imgs"><img src={book.cover_picture} alt="#" /></div>
              <div><button>Add to favorites</button></div>
            </div>
          ))
        }
      </div>
    );
  };
  
  export default BookList;
