import React, { useState, useEffect } from "react";
import axios from 'axios';
import { API_URL } from '../API';
import { useAppContext } from "./context/appContext";
import '../App.css';
import { useNavigate } from "react-router-dom";

const BookList = () => {
  const [books, setBooks] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(''); // State to manage selected category
  const { favorites, addToFavorites, removeFromFavorites } = useAppContext();
  const navigate = useNavigate();

  const favoritesChecker = (id) => {
    return favorites.some((book) => book.id === id);
  }

  useEffect(() => {
    // Fetch books and categories initially
    axios.get(API_URL)
      .then(res => {
        setBooks(res.data.books); // Assuming API returns books under 'books' key
        setCategories(res.data.categories); // Assuming API returns categories under 'categories' key
      })
      .catch(err => console.log(err));
  }, []);

  useEffect(() => {
    // Filter books based on the selected category
    let filteredBooks = books;
    if (selectedCategory) {
      filteredBooks = books.filter(book => book.category === selectedCategory);
    }
    setBooks(filteredBooks);
  }, [selectedCategory]);

  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
  }

  return (
    <div>
<div>
      {/* Dropdown to select category */}
      <select value={selectedCategory} onChange={handleCategoryChange}>
        <option value="">All Categories</option>
        {categories.map(category => (
          <option key={category.id} value={category.name}>
            {category.name}
          </option>
        ))}
      </select>
    </div>
    <div className="book-list">
      {books && books.length > 0 ? (
        books.map((book) => (
          <div key={book.id} className="book">
            {/* ... Rest of your book rendering code */}
          </div>
        ))
      ) : (
        <p>No books found</p>
      )}
    </div>
      <div className="book-list">
        {books.map((book) => (
          <div key={book.id} className="book">
            <div><h3>{book.title}</h3></div>
            <div className="imgs">
              <img
                src={book.cover_picture}
                alt="#"
                onClick={() => navigate(`/books/${book.id}`, { state: { book } })}
              />
            </div>
            <div>
              {favoritesChecker(book.id) ? (
                <button onClick={() => removeFromFavorites(book)}>
                  Remove From Favorites
                </button>
              ) : (
                <button onClick={() => addToFavorites(book)}>
                  Add to Favorites
                </button>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};



export default BookList;
