import React from "react";
import "../App.css";
import { useState, useEffect } from "react";
import axios from 'axios';
import { API_URL } from '../API';
import { useAppContext } from "./context/appContext";

const BookList = () => {
  const [books, setBooks] = useState([]);

const{favorites,addToFavorites,removeFromFavorites}=useAppContext();

console.log( favorites);

useEffect(() => {
  axios.get(API_URL)
    .then(res => {
      if (Array.isArray(res.data) && res.data.length > 0) {
        setBooks(res.data);
      } else {
        console.log("No data received or unexpected data structure");
        // Handle the empty data or unexpected structure scenario here
        // For instance, set an error message or render a message on the page
      }
    })
    .catch(err => console.log(err));
}, []);
  return (

      <div className="book-list">
        {
          books.map((book) => (
            <div key={book.id} className="book">
              <div><h3>{book.title}</h3></div>
              <div className="imgs"><img src={book.cover_picture} alt="#" /></div>
              <div><button onClick={()=>addToFavorites(book)}>Add to favorites</button></div>
            </div>
          ))
        }
      </div>
 
  );
};

export default BookList;
